import React, { useState, useEffect } from 'react';
import axios from 'axios';

function EnhancedPrediction({ symptoms }) {
  const [selectedSymptoms, setSelectedSymptoms] = useState([]);
  const [severityData, setSeverityData] = useState({});
  const [prediction, setPrediction] = useState(null);
  const [triage, setTriage] = useState(null);
  const [explanation, setExplanation] = useState(null);
  const [nextSymptoms, setNextSymptoms] = useState([]);
  const [validation, setValidation] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [feedback, setFeedback] = useState({
    rating: 5,
    comment: '',
    actualDisease: ''
  });
  const [feedbackSubmitted, setFeedbackSubmitted] = useState(false);

  // Debug log
  console.log('EnhancedPrediction received symptoms:', symptoms);

  const severityLevels = [
    { value: 1, label: 'Mild', color: '#4CAF50' },
    { value: 2, label: 'Moderate', color: '#FF9800' },
    { value: 3, label: 'Severe', color: '#F44336' }
  ];

  const handleSymptomToggle = (symptom) => {
    if (selectedSymptoms.includes(symptom)) {
      setSelectedSymptoms(selectedSymptoms.filter(s => s !== symptom));
      const newSeverity = { ...severityData };
      delete newSeverity[symptom];
      setSeverityData(newSeverity);
    } else {
      setSelectedSymptoms([...selectedSymptoms, symptom]);
      setSeverityData({ ...severityData, [symptom]: 1 });
    }
  };

  const handleSeverityChange = (symptom, severity) => {
    setSeverityData({ ...severityData, [symptom]: parseInt(severity) });
  };

  const handleEnhancedPredict = async () => {
    if (selectedSymptoms.length === 0) {
      setError('Please select at least one symptom');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      // Enhanced prediction
      const predictionRes = await axios.post('/api/predict', {
        symptoms: selectedSymptoms,
        severity_data: severityData
      });
      
      setPrediction(predictionRes.data);

      // Get triage information
      const triageRes = await axios.post('/api/triage', {
        symptoms: selectedSymptoms,
        severity_data: severityData
      });
      
      setTriage(triageRes.data);

      // Get explanation
      if (predictionRes.data.predicted_disease) {
        const explanationRes = await axios.post('/api/explain', {
          symptoms: selectedSymptoms,
          predicted_disease: predictionRes.data.predicted_disease
        });
        
        setExplanation(explanationRes.data);
      }

      // Get next symptom suggestions
      const nextRes = await axios.post('/api/suggest-next', {
        symptoms: selectedSymptoms
      });
      
      setNextSymptoms(nextRes.data.suggested_symptoms || []);

    } catch (err) {
      setError(err.response?.data?.error || 'Prediction failed');
    } finally {
      setLoading(false);
    }
  };

  const handleValidateSymptoms = async () => {
    if (selectedSymptoms.length === 0) return;

    try {
      const res = await axios.post('/api/validate', {
        symptoms: selectedSymptoms
      });
      
      setValidation(res.data);
    } catch (err) {
      console.error('Validation error:', err);
    }
  };

  const handleSubmitFeedback = async () => {
    if (!prediction) return;

    try {
      await axios.post('/api/feedback', {
        symptoms: selectedSymptoms,
        predicted_disease: prediction.predicted_disease,
        actual_disease: feedback.actualDisease,
        confidence: prediction.confidence,
        triage_level: triage?.triage_level,
        user_rating: feedback.rating,
        feedback_text: feedback.comment
      });
      
      setFeedbackSubmitted(true);
    } catch (err) {
      console.error('Feedback error:', err);
    }
  };

  useEffect(() => {
    if (selectedSymptoms.length > 0) {
      handleValidateSymptoms();
    }
  }, [selectedSymptoms]);

  const getTriageColor = (level) => {
    switch (level) {
      case 'EMERGENCY': return '#D32F2F';
      case 'URGENT': return '#F57C00';
      case 'ROUTINE': return '#388E3C';
      default: return '#757575';
    }
  };

  // Check if symptoms are loaded
  if (!Array.isArray(symptoms) || symptoms.length === 0) {
    return (
      <div className="enhanced-prediction">
        <h2>‚ö° Enhanced Disease Prediction</h2>
        <p>Advanced prediction with severity tracking, triage assessment, and explainable AI</p>
        <div className="loading-message">
          <p>üîÑ Loading symptoms...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="enhanced-prediction">
      <h2>‚ö° Enhanced Disease Prediction</h2>
      <p>Advanced prediction with severity tracking, triage assessment, and explainable AI</p>

      {error && (
        <div className="error-message">
          <span>‚ö†Ô∏è</span>
          {error}
        </div>
      )}

      {/* Symptom Selection with Severity */}
      <div className="symptom-section">
        <h3>Select Symptoms with Severity</h3>
        
        <div className="symptom-search">
          <input
            type="text"
            placeholder="Search symptoms..."
            onChange={(e) => {
              const query = e.target.value.toLowerCase();
              // Filter symptoms based on search
            }}
          />
        </div>

        <div className="symptom-grid">
          {symptoms && symptoms.length > 0 ? (
            symptoms.slice(0, 20).map(symptom => (
              <div key={symptom} className="symptom-card">
                <label className="symptom-checkbox">
                  <input
                    type="checkbox"
                    checked={selectedSymptoms.includes(symptom)}
                    onChange={() => handleSymptomToggle(symptom)}
                  />
                  <span className="symptom-name">{symptom.replace(/_/g, ' ')}</span>
                </label>
                
                {selectedSymptoms.includes(symptom) && (
                  <div className="severity-selector">
                    <label>Severity:</label>
                    <select
                      value={severityData[symptom] || 1}
                      onChange={(e) => handleSeverityChange(symptom, e.target.value)}
                    >
                    {severityLevels.map(level => (
                      <option key={level.value} value={level.value}>
                        {level.label}
                      </option>
                    ))}
                  </select>
                </div>
              )}
            </div>
          ))
          ) : (
            <div className="loading-message">Loading symptoms...</div>
          )}
        </div>
      </div>

      {/* Selected Symptoms Summary */}
      {selectedSymptoms.length > 0 && (
        <div className="selected-summary">
          <h4>Selected Symptoms ({selectedSymptoms.length})</h4>
          <div className="symptom-tags">
            {selectedSymptoms.map(symptom => (
              <span key={symptom} className="symptom-tag">
                {symptom.replace(/_/g, ' ')}
                <span className="severity-badge" style={{
                  backgroundColor: severityLevels[severityData[symptom] - 1]?.color
                }}>
                  {severityLevels[severityData[symptom] - 1]?.label}
                </span>
              </span>
            ))}
          </div>
        </div>
      )}

      {/* Validation Results */}
      {validation && validation.invalid_symptoms && validation.invalid_symptoms.length > 0 && (
        <div className="validation-results">
          <h4>‚ö†Ô∏è Symptom Validation</h4>
          <p>Some symptoms might have better matches:</p>
          {Object.entries(validation.suggestions || {}).map(([invalid, suggestions]) => (
            <div key={invalid} className="suggestion">
              <span>"{invalid}" ‚Üí </span>
              {suggestions.map(suggestion => (
                <button
                  key={suggestion}
                  className="suggestion-btn"
                  onClick={() => {
                    const newSymptoms = selectedSymptoms.filter(s => s !== invalid);
                    newSymptoms.push(suggestion);
                    setSelectedSymptoms(newSymptoms);
                  }}
                >
                  {suggestion}
                </button>
              ))}
            </div>
          ))}
        </div>
      )}

      {/* Predict Button */}
      <div className="actions">
        <button
          className="enhanced-predict-btn"
          onClick={handleEnhancedPredict}
          disabled={selectedSymptoms.length === 0 || loading}
        >
          {loading ? 'üîÑ Analyzing...' : 'üöÄ Enhanced Predict'}
        </button>
      </div>

      {/* Results */}
      {prediction && (
        <div className="prediction-results">
          {/* Main Prediction */}
          <div className="prediction-card">
            <h3>üî¨ Prediction Result</h3>
            <div className="disease-result">
              <span className="disease-name">{prediction.predicted_disease}</span>
              <span className="confidence">
                {(prediction.confidence * 100).toFixed(1)}% confidence
              </span>
            </div>
            
            {prediction.description && (
              <div className="disease-description">
                <h4>Description</h4>
                <p>{prediction.description}</p>
              </div>
            )}
            
            {prediction.precautions && (
              <div className="precautions">
                <h4>Precautions</h4>
                <ul>
                  {prediction.precautions.map((precaution, idx) => (
                    <li key={idx}>{precaution}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* Triage Assessment */}
          {triage && (
            <div className="triage-card">
              <h3>üö® Triage Assessment</h3>
              <div className="triage-level" style={{ 
                borderLeft: `4px solid ${getTriageColor(triage.triage_level)}` 
              }}>
                <span className="level">{triage.triage_level}</span>
                <span className="score">Score: {triage.urgency_score && triage.urgency_score.toFixed(1)}</span>
              </div>
              <p className="triage-recommendation">{triage.recommendation}</p>
              {triage.emergency_symptoms && triage.emergency_symptoms.length > 0 && (
                <div className="emergency-symptoms">
                  <strong>‚ö†Ô∏è Emergency symptoms detected:</strong>
                  <ul>
                    {triage.emergency_symptoms.map((symptom, idx) => (
                      <li key={idx}>{symptom}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          )}

          {/* Explanation */}
          {explanation && (
            <div className="explanation-card">
              <h3>üß† AI Explanation</h3>
              <div className="explanation-content">
                <h4>How we arrived at this prediction:</h4>
                <p>{explanation.explanation}</p>
                
                {explanation.feature_importance && (
                  <div className="feature-importance">
                    <h4>Key factors:</h4>
                    <div className="importance-bars">
                      {Object.entries(explanation.feature_importance)
                        .slice(0, 5)
                        .map(([feature, importance]) => (
                          <div key={feature} className="importance-bar">
                            <span className="feature-name">{feature.replace(/_/g, ' ')}</span>
                            <div className="bar-container">
                              <div 
                                className="bar" 
                                style={{ width: `${importance * 100}%` }}
                              ></div>
                            </div>
                            <span className="importance-value">
                              {(importance * 100).toFixed(1)}%
                            </span>
                          </div>
                        ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Next Symptoms */}
          {nextSymptoms.length > 0 && (
            <div className="next-symptoms-card">
              <h3>üí° Consider These Symptoms</h3>
              <p>Based on your current symptoms, you might also want to check for:</p>
              <div className="next-symptom-suggestions">
                {nextSymptoms.map(symptom => (
                  <button
                    key={symptom}
                    className="next-symptom-btn"
                    onClick={() => handleSymptomToggle(symptom)}
                    disabled={selectedSymptoms.includes(symptom)}
                  >
                    {symptom.replace(/_/g, ' ')}
                    {selectedSymptoms.includes(symptom) && ' ‚úì'}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Feedback Section */}
          {!feedbackSubmitted && (
            <div className="feedback-card">
              <h3>üìù Provide Feedback</h3>
              <div className="feedback-form">
                <div className="rating-section">
                  <label>How accurate was this prediction?</label>
                  <div className="star-rating">
                    {[1, 2, 3, 4, 5].map(star => (
                      <button
                        key={star}
                        className={`star ${star <= feedback.rating ? 'active' : ''}`}
                        onClick={() => setFeedback({...feedback, rating: star})}
                      >
                        ‚≠ê
                      </button>
                    ))}
                  </div>
                </div>
                
                <div className="actual-disease-section">
                  <label>If different, what was the actual diagnosis?</label>
                  <input
                    type="text"
                    value={feedback.actualDisease}
                    onChange={(e) => setFeedback({...feedback, actualDisease: e.target.value})}
                    placeholder="Enter actual disease (if known)"
                  />
                </div>
                
                <div className="comment-section">
                  <label>Additional comments:</label>
                  <textarea
                    value={feedback.comment}
                    onChange={(e) => setFeedback({...feedback, comment: e.target.value})}
                    placeholder="Any additional feedback..."
                    rows="3"
                  />
                </div>
                
                <button className="feedback-submit-btn" onClick={handleSubmitFeedback}>
                  Submit Feedback
                </button>
              </div>
            </div>
          )}

          {feedbackSubmitted && (
            <div className="feedback-success">
              <h3>‚úÖ Thank you for your feedback!</h3>
              <p>Your input helps improve our AI model.</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default EnhancedPrediction;
